mapper.insert:
INSERT INTO t_user (
username, password, salt, email, is_delete, 
created_user, created_time, modified_user, modified_time
) VALUES (
#{username}, #{password}, #{salt}, #{email}, #{is_delete}, 
#{created_user}, #{created_time}, #{modified_user},
#{modified_time}
)

mapper.select:
SELECT id,username, password, salt, email, is_delete, 
created_user, created_time, modified_user, modified_time FROM t_user
WHERE username = #{username}

SELECT id,username, password, salt, email, is_delete, 
created_user, created_time, modified_user, modified_time FROM t_user
WHERE id = #{id}
